$(document).ready(function () {
  // Function to set cookie
  function setCookie(name, value, days) {
    const date = new Date();
    date.setTime(date.getTime() + days * 24 * 60 * 60 * 1000);
    const expires = "expires=" + date.toUTCString();
    document.cookie = name + "=" + value + ";" + expires + ";path=/";
  }

  // Function to get cookie
  function getCookie(name) {
    const decodedCookie = decodeURIComponent(document.cookie);
    const cookies = decodedCookie.split(";");
    name = name + "=";
    for (let i = 0; i < cookies.length; i++) {
      let c = cookies[i].trim();
      if (c.indexOf(name) === 0) {
        return c.substring(name.length, c.length);
      }
    }
    return null;
  }

  // Check and handle modal display
  const persetujuanCookie = getCookie("persetujuanPenggunaan");
  const welcomeMessage = $("#welcomeModal");

  if (!persetujuanCookie) {
    // Show modal if cookie doesn't exist
    welcomeMessage.modal("show");
  } else {
    // Show welcome message if cookie exists
    if (sessionStorage.getItem("firstVisit") === null) {
      welcomeMessage.modal("show");
      sessionStorage.setItem("firstVisit", "false");
    }
  }
  // Set cookie on accept button click
  $("#setujuBtn").on("click", function () {
    setCookie("persetujuanPenggunaan", "true", 30); // Cookie valid for 30 days
    welcomeMessage.modal("hide");
    welcomeMessage.css("display", "block");
  });
});
